insert into PRODUCT(BRANCH_ID, CATEGORY_ID, NAME, SUBTITLE, URL, PRICE, VIEW, SOLD, DETAIL, QUANTITY, VERSION) values(null,5,'Cable iPhone 7 Chính Hãng','','cable-iphone-7-zin',250000,0,0,'{"images":{"selected":"default","items":{"default":"cable-iphone-7-zin.png"}},"desc":"Apple Lightning 1m có thiết kế đơn giản, dây cáp thanh mảnh dài 1m nên khá nhẹ. Dòng Cáp sạc Apple Lightning chính hãng được sản xuất với dây chuyền dựa trên công nghệ cao của Apple. Sử dụng chất liệu tốt giúp việc truyền tải điện năng nhanh hơn. Đối với những loại sạc lightning kém chất lượng, không rõ nguồn gốc sẽ gây nguy hại không chỉ cho thiết bị mà cho người sử dụng. Đã có nhiều trường hợp tại nạn cháy nổ khi sạc iPhone là do dây cáp kém chất lượng, dễ bắt lửa. Đối với cáp sạc Apple Lightning được Apple sản xuất theo quy trình tiêu chuẩn và mỗi sợi cáp đều được kiểm tra nghiêm ngặt, kỹ lưỡng trước khi đến tay người tiêu dùng.","spec":null}',100,0);
insert into PRODUCT(BRANCH_ID, CATEGORY_ID, NAME, SUBTITLE, URL, PRICE, VIEW, SOLD, DETAIL, QUANTITY, VERSION) values(null,5,'Cable TITAN iPhone 5','','cable-titan-iphone-5',99000,0,0,'{"images":{"selected":"default","items":{"default":"cable-titan-iphone-5.png"}},"desc":"Dây cáp chân lightning dành cho các dòng điện thoại iPhone 5/6/7/8. Dài 1m. Có thiết kế nhỏ gọn, chắc chắn, thân cáp bọc nhựa dẻo không gây độc hại, có thể cuộn tròn lại thuận tiện mang theo trong cặp, túi xách để sử dụng hàng ngày. Chống rối, chống đứt ngầm. Cổng kết nối USB 2.0 giúp thiết bị chuyển dữ liệu như nhạc, hình, clip hay cài đặt phần mềm qua lại với máy tính trở nên dễ dàng và thuận tiện. Sử dụng lõi đồng nguyên chất, cho ra đúng nguồn điện, ngăn ngừa xung điện, tránh chai pin.","spec":null}',100,0);
insert into PRODUCT(BRANCH_ID, CATEGORY_ID, NAME, SUBTITLE, URL, PRICE, VIEW, SOLD, DETAIL, QUANTITY, VERSION) values(null,5,'Củ Sạc iPad Chính Hãng','','sac-ipad-zin-5w-usb-power',300000,0,0,'{"images":{"selected":"default","items":{"default":"sac-ipad-zin-5w-usb-power.png"}},"desc":"Sản phẩm đến từ Apple luôn đạt chất lượng tốt và được người dùng đánh giá cao không chỉ mang lại trải nghiệm tốt mà còn vì an toàn khi sử dụng. Củ sạc Apple 12W USB Power là phụ kiện tương thích phổ biến nhất hiện nay của Apple dành cho người dùng iPhone, iPad và iPod. Người dùng sẽ an tâm hơn khi chọn mua một củ sạc Apple 12W USB Power chính hãng đến từ Apple và được phân phối phối chính thức tại Di Động Thông Minh, bởi vì so với những củ sạc trôi nổi kém chất lượng, không rõ nguồn gốc thì nguy cơ hư hỏng và cháy nổ rất là cao, không chỉ gây hư hại cho thiết bị mà còn có thể gây nguy hiểm đến sức khỏe người sử dụng.","spec":null}',100,0);
insert into PRODUCT(BRANCH_ID, CATEGORY_ID, NAME, SUBTITLE, URL, PRICE, VIEW, SOLD, DETAIL, QUANTITY, VERSION) values(null,5,'Sạc TITAN 2A chính hãng','','sac-titan-2a-chinh-hang',200000,0,0,'{"images":{"selected":"default","items":{"default":"sac-titan-2a-chinh-hang.png"}},"desc":"Sạc Titan 2A Sở hữu kiểu dáng khá nhỏ gọn và kích thước nhẹ, tông nền trắng bắt mắt tạo sự hài hòa hơn khi phối kèm với các thiết bị. Sử Công nghệ sạc nhanh Ultra Fast độc quyền được tích hợp giúp các thiết bị sử dụng cung cấp nguồn năng lượng nhanh hơn, tiết kiệm được tối đa thời gian cho người dùng. Cáp được cấu tạo từ những lõi dây bên trong được bện chặt chẽ mang đến hiệu năng sử dụng lâu hơn và đặc biệt không gây độc hại cho người sử dụng.Có thể sạc nhanh cho mọi thiết bị (đặc biệt là iphone, sạc đầy pin cho iPhone chỉ trong vòng 40p). Ngoài ra, sản phẩm còn sạc tốt cho cả các dòng như là iPad, Samsung, Oppo…","spec":null}',100,0);
insert into PRODUCT(BRANCH_ID, CATEGORY_ID, NAME, SUBTITLE, URL, PRICE, VIEW, SOLD, DETAIL, QUANTITY, VERSION) values(null,5,'Sạc TITAN 1A chính hãng','','sac-titan-1a-chinh-hang',119000,0,0,'{"images":{"selected":"default","items":{"default":"sac-titan-1a-chinh-hang.png"}},"desc":"Sạc TITAN 1A được là sản phẩm chính hãng được sản xuất bởi công nghệ Việt Nam, nhằm mang đến cho người dùng các dòng điện thoại Smart Phone một linh kiện sạc tương thích và linh hoạt. sạc TITAN 1A xứng đáng là hàng Việt Nam chất lượng cao, hiện đang là một trong các linh kiện sạc điện thoại được rất nhiều người dùng lựa chọn cho dế yêu của mình. Được sản xuất với dây chuyền công nghệ Việt Nam, Sạc TITAN 1A được thiết kế linh hoạt, không chỉ phù hợp tất cả những dòng điện thoại Smart phone mà còn có thể sử dụng cho máy tính bảng, các sản phẩm có cổng USB. Sạc TITAN 1A sở hữu kiểu dáng nhỏ gọn, kích thước nhẹ bên cạnh đó là màu trắng bắt mắt tạo sự hài hòa và sang trọng hơn khi sử dụng chung với các thiết bị di động khác.","spec":null}',100,0);
insert into PRODUCT(BRANCH_ID, CATEGORY_ID, NAME, SUBTITLE, URL, PRICE, VIEW, SOLD, DETAIL, QUANTITY, VERSION) values(null,5,'Cable Samsung Chính Hàng','','cable-samsung-chinh-hang',99000,0,0,'{"images":{"selected":"default","items":{"default":"cable-samsung-chinh-hang.png"}},"desc":"Cáp micro USB thường hay bị người dùng để quên, làm rơi dẫn đến việc phải mua một sợi cable mới thay thế. Trong trường hợp này, cáp Samsung Micro USB 1.5m là sự lựa chọn rất xứng đáng để bạn phải cân nhắc. Sợi cable này được hoàn thiện từ vật liệu cao cấp cho khả năng chịu lực tốt, không bị gãy nứt hay bong tróc lớp vỏ khi bị uốn cong hoặc va đập mạnh nhiều lần trong quá trình sử dụng. Không chỉ sử dụng cho các thiết bị di động của Samsung mà tương thích tốt với mọi điện thoại/máy tính bảng/máy nghe nhạc hỗ trợ cổng micro USB","spec":null}',100,0);
