insert into PRODUCT(BRANCH_ID, CATEGORY_ID, NAME, SUBTITLE, URL, PRICE, VIEW, SOLD, DETAIL, QUANTITY, VERSION) values(null,5,'Cable XO IPhone','','cable-xo-iphone',100000,0,0,'{"images":{"selected":"default","items":{"default":"cable-xo-iphone.png"}},"desc":"Cáp iPhone chất lượng cao , đạt tiêu chuẩn MFI ( Made For iDevice ) của Apple. Cáp sạc ổn định cho mọi model iPhone , không gây đơ , loạn cảm ứng , chống chai pin , bảo vệ an toàn cho iPhone cũng như người sử dụng. Chất lượng tương đương cáp sạc xịn Apple. Thiết kế thời trang, hiện đại. Sạc tương thích với các loại iPhone, ipod","spec":null}',100,0);
