insert into PRODUCT(BRANCH_ID, CATEGORY_ID, NAME, SUBTITLE, URL, PRICE, VIEW, SOLD, DETAIL, QUANTITY, VERSION) values(null,5,'Cable Sony Chính Hãng','','cable-sony-chinh-hang',100000,0,0,'{"images":{"selected":"default","items":{"default":"cable-sony-chinh-hang.png"}},"desc":"Cáp Sony với đường kính rộng hơn giúp sạc nhanh hơn. Với các thiết bị tương thích, cáp USB của Sony hỗ trợ đến 2,4 A ở ngõ vào/ngõ ra để sạc nhanh hơn tới năm lần so với các cáp khác. Cáp USB của Sony có thời gian sử dụng gấp đôi các hãng khác nhờ có đường kính rộng và vật liệu chế tạo tốt. Lõi ferit giúp giảm nhiễu điện cho truyền tải tín hiệu tối ưu. Bạn có thể sử dụng để truyền dữ liệu giữa các thiết bị USB Type-A và Micro USB.","spec":null}',100,0);
