insert into PRODUCT(BRANCH_ID, CATEGORY_ID, NAME, SUBTITLE, URL, PRICE, VIEW, SOLD, DETAIL, QUANTITY, VERSION) values(null,5,'Cable Samsung Type-C','','cable-samsung-type-c',150000,0,0,'{"images":{"selected":"default","items":{"default":"cable-samsung-type-c.png"}},"desc":"Nhìn vào hình thức thiết kế bên ngoài thì không khác gì so với các dòng cable kết nối Samsung USB 2.0 trước đây, chỉ khác nhau ở chỗ thay vì sử dụng MicroUSB 2.0 thánh USB Type C. Cable là hàng chính hãng Samsung được sản xuất tại Việt Nam. Không chỉ sử dụng cho các thiết bị di động của Samsung mà tương thích tốt với mọi điện thoại/máy tính bảng/máy nghe nhạc hỗ trợ cổng type-C.","spec":null}',100,0);
