insert into PRODUCT(BRANCH_ID, CATEGORY_ID, NAME, SUBTITLE, URL, PRICE, VIEW, SOLD, DETAIL, QUANTITY, VERSION) values(null,5,'Cable LG Type-C Chính Hãng','','cable-lg-type-c-chinh-hang',150000,0,0,'{"images":{"selected":"default","items":{"default":"cable-lg-type-c-chinh-hang.png"}},"desc":"Cable LG Type-C Cao Cấp Chính Hãng là phụ kiện nhỏ gọn, đẹp mắt và tiện dụng dành cho người dùng điện thoại. Mua dây cáp sạc USB Type-C LG chính hãng giá rẻ là đầu tư hợp lí. Là hàng chính hãng LG, có độ dài của cáp: 120 cm. Tương thích với mọi thiết bị sạc sử dụng cổng sạc Micro USB và các loại smartphone sử dụng cổng type-C","spec":null}',100,0);
insert into PRODUCT(BRANCH_ID, CATEGORY_ID, NAME, SUBTITLE, URL, PRICE, VIEW, SOLD, DETAIL, QUANTITY, VERSION) values(null,5,'Cable iPhone 5 Cao Cấp','','cable-iphone-5-cao-cap',150000,0,0,'{"images":{"selected":"default","items":{"default":"cable-iphone-5-cao-cap.png"}},"desc":"Dây cáp cao cấp sản xuất dành riêng cho dòng Iphone 5 trở lên, đồng thời có thể sạc cho các đời iPad. Tương đương gần bằng với dòng cáp xịn của Apple. Cấu trúc lõi dây hoàn hảo, giúp sạc nhanh thiết bị. Ngăn ngừa xung điện cho thiết bị. Dây cáp bọc nhựa dẻo không độc hại đến người sử dụng.","spec":null}',100,0);
insert into PRODUCT(BRANCH_ID, CATEGORY_ID, NAME, SUBTITLE, URL, PRICE, VIEW, SOLD, DETAIL, QUANTITY, VERSION) values(null,5,'Sạc Nhanh LG V10','','sac-nhanh-lg-v10',250000,0,0,'{"images":{"selected":"default","items":{"default":"sac-nhanh-lg-v10.png"}},"desc":"LG V10 hỗ trợ sạc nhanh QuickCharge 2.0 của Qualcomm. Điện ra trên cục sạc nhanh là 9V – 1.8A và 5V – 18A có thể sạc đầy 50% pin trong vòng 28 phút và 100% với 85 phút. Với chuẩn này thì chúng ta có thể dùng cục sạc này để sạc nhanh cho các máy được hỗ trợ, hoặc có thể dùng để sạc cho các dòng máy chuẩn 5V – 1.8A ( Cục sạc có chip để nhận biết và giảm nguồn ) . Trước đây G4 LG không công bố sạc nhanh nhưng cũng hỗ trợ sạc nhanh đó , với V10 thì LG công bố luôn và bán kèm cục sạc có hỗ trợ sạc nhanh luôn. Sạc Tương thích với các loại Smartphone Cao cấp hiện nay.","spec":null}',100,0);
insert into PRODUCT(BRANCH_ID, CATEGORY_ID, NAME, SUBTITLE, URL, PRICE, VIEW, SOLD, DETAIL, QUANTITY, VERSION) values(null,5,'Cable iPhone 4 Cao Cấp','','cable-iphone-4-cao-cap',80000,0,0,'{"images":{"selected":"default","items":{"default":"cable-iphone-4-cao-cap.png"}},"desc":"Dây cáp cao cấp sản xuất dành riêng cho dòng iPhone 4/4s đồng thời có thể sạc cho các đời iPad 1/2/3. Tương đương gần bằng với dòng cáp xịn của Apple. Cấu trúc lõi dây hoàn hảo, giúp sạc nhanh thiết bị. Ngăn ngừa xung điện cho thiết bị. Dây cáp bọc nhựa dẻo không độc hại đến người sử dụng.","spec":null}',100,0);
insert into PRODUCT(BRANCH_ID, CATEGORY_ID, NAME, SUBTITLE, URL, PRICE, VIEW, SOLD, DETAIL, QUANTITY, VERSION) values(null,5,'Sạc Sony Chính Hãng','','sac-sony-chinh-hang',150000,0,0,'{"images":{"selected":"default","items":{"default":"sac-sony-chinh-hang.png"}},"desc":"Sony là một tập đoàn đa ngành đặt trụ sở tại Nhật Bản. Trải qua nhiều năm thành lập và phát triển, đến nay hãng đã trở thành một tên tuổi lớn ở nhiều lĩnh vực khác nhau trên toàn thế giới, trong đó có phụ kiện smartphone. Sạc Sony chính hãng có thiết kế nhỏ gọn (kích thước 53 x 40 x 20mm và trọng lượng chỉ 36g), mang lại sự thuận tiện trong quá trình sử dụng. Sạc Sony chính hãng được tích hợp công nghệ sạc nhanh “Quick Charger” và sử dụng chân đồng ở lõi pin cho khả năng dẫn điện tốt, giúp người dùng nhanh chóng nạp đầy pin cho điện thoại hoặc máy tính bảng, đặc biệt là những thiết bị có hỗ trợ sạc nhanh. Với IC thông minh tự động điều khiển dòng ra sao cho phù hợp nhất với thiết bị được cắm sạc, sạc Sony chính hãng tương thích tốt với tất cả điện thoại Sony và sản phẩm của nhiều nhà sản xuất khác nhau như: HTC, LG, Samsung, Nokia, Lenovo, Xiaomi…","spec":null}',100,0);
