insert into PRODUCT(BRANCH_ID, CATEGORY_ID, NAME, SUBTITLE, URL, PRICE, VIEW, SOLD, DETAIL, QUANTITY, VERSION) values(null,4,'Sạc Dự Phòng Remax RPP- 88 10000mAh Chính Hãng','','sac-du-phong-remax-rpp-88',399000,0,0,'{"images":{"selected":"default","items":{"default":"sac-du-phong-remax-rpp-88.png"}},"desc":"Sạc Dự Phòng Remax RPP-88 10000mAh được thiết kế đơn giản với hình khối chữ nhật màu trắng hoặc đen, thể hiện sự thời trang, tinh tế và hiện đại cho người sử dụng. Với hiệu suất 37Wh/3.7V, giúp bạn thoải mái sạc đầy pin cho thiết bị của mình trong khoảng thời gian trung bình từ 3 – 4 lần, Có sạc nhanh quick charge, sử dụng lõi pin chuyên dụng : Lithium Polymer","spec":null}',100,0);
