insert into PRODUCT(BRANCH_ID, CATEGORY_ID, NAME, SUBTITLE, URL, PRICE, VIEW, SOLD, DETAIL, QUANTITY, VERSION) values(null,4,'Pin Sạc Dự Phòng Xiaomi 20.000mAh Gen2 Chính Hãng','','sac-du-phong-xiaomi-10-000mah-chinh-hang',599000,0,0,'{"images":{"selected":"default","items":{"default":"sac-du-phong-xiaomi-10-000mah-chinh-hang.png"}},"desc":"Pin Sạc Dự Phòng Xiaomi 20.000 mAh có bề dày chỉ 2cm, chiều dài chỉ nhỉnh hơn 1 chút so với chiếc iPhone 6 và nặng 318 gram. Sử dụng lõi pin của hãng LG (6 cells) và được tích hợp mạch bảo vệ an toàn (sử dụng chip của hãng Texas Intrusment). Đầu vào: DC 5 V/2 A 9 V/2 A 12 V/1.5 A. Đầu ra: 2x DC 5.1 V/2 A MAX 3.6 A. Tương thích hầu hết các thiết bị di động hiện nay như, điện thoại thông minh, máy tính bảng, máy ảnh từ các thương hiệu như Apple, Samsung, Xiaomi, HTC… sạc được cả tay cầm game","spec":null}',100,0);
insert into PRODUCT(BRANCH_ID, CATEGORY_ID, NAME, SUBTITLE, URL, PRICE, VIEW, SOLD, DETAIL, QUANTITY, VERSION) values(null,4,'Pin Sạc Dự Phòng Xiaomi Gen2 10000 mAh (2018)','','sac-du-phong-xiaomi-gen-2-10000-mah-2018',399000,0,0,'{"images":{"selected":"default","items":{"default":"sac-du-phong-xiaomi-gen-2-10000-mah-2018.png"}},"desc":"Pin Sạc Dự Phòng Xiaomi Gen 2 2018 là 1 phiên bản nâng cấp với rất nhiều cải tiến đáng chú ý, hứa hẹn sẽ mang đến cho người dùng những trải nghiệm mới hoàn hảo hơn. Phiên bản mới lần này không chỉ có sự thay đổi về ngoại hình mà cả về tính năng cũng có nhiều nâng cấp đáng chú ý. Dung lượng khủng 10000mAh 2 USB đầu ra giúp bạn có thể sạc cùng lúc 2 thiết bị. Siêu nhỏ nhẹ, với kích thước 147×71.2×14.2mm. Sạc nhanh 2 chiều cả vào pin và cả sạc ra cho các thiết bị khác (chuẩn sạc nhanh Quick Charge 3.0 mới nhất của Qualcomm, công suất 18W). Lõi pin Lithium Polymer. Tương thích hầu hết các thiết bị di động hiện nay như điện thoại, máy tính bảng, máy ảnh từ các thương hiệu như Apple, Samsung, Xiaomi, HTC…. Sử dụng vi mạch điều khiển cao cấp với 9 lớp bảo vệ an toàn (chống đoản mạch, quá nhiệt…)","spec":null}',100,0);
insert into PRODUCT(BRANCH_ID, CATEGORY_ID, NAME, SUBTITLE, URL, PRICE, VIEW, SOLD, DETAIL, QUANTITY, VERSION) values(null,4,'Pin Sạc Dự Phòng Xiaomi Gen2 10000 mAh (2017)','','sac-du-phong-xiaomi-gen-2-10000-mah-2017',359000,0,0,'{"images":{"selected":"default","items":{"default":"sac-du-phong-xiaomi-gen-2-10000-mah-2017.png"}},"desc":"Với sự nỗ lực sử dụng công nghệ mới nhất Sạc Dự Phòng Xiaomi Gen 2 2017 sở hữu độ mỏng vô cùng ấn tượng 14.1mm và nặng hơn 200g, một điều không tưởng đối với các sạc dự phòng khác cùng dung lượng. Có công nghệ sạc đa chiều (chiều vào cho pin và sạc ra cho điện thoại máy tính bảng). Sử dụng lõi pin ổn định (Pin lithium polymer). Vỏ nhôm nguyên khối, Tương thích hầu hết các thiết bị di động hiện nay như, điện thoại thông minh, máy tính bảng, máy ảnh từ các thương hiệu như Apple, Samsung, Xiaomi, HTC,… sạc được cả tay cầm game…","spec":null}',100,0);
insert into PRODUCT(BRANCH_ID, CATEGORY_ID, NAME, SUBTITLE, URL, PRICE, VIEW, SOLD, DETAIL, QUANTITY, VERSION) values(null,4,'Sạc Dự Phòng Microcom 6.000 mAh','','sac-du-phong-microcom-6000mah',199000,0,0,'{"images":{"selected":"default","items":{"default":"sac-du-phong-microcom-6000mah.png"}},"desc":"Sản phẩm pin dự phòng Microcom 6000mAh ở Di Động Thông Minh là lựa chọn thông minh và hữu ích. Thiết bị sở hữu thiết kế nhỏ gọn, vừa vặn trong long bàn tay cùng 3 màu thời trang để lựa chọn như đỏ, xanh hay đen. Với màu sắc rất tinh tế và thời trang cùng với kích thước vừa cầm tay sẽ giúp người dùng khá thoải mái khi cầm tay vừa sạc điện thoại vừa di chuyển. Bên cạnh đó, với thương hiệu Microcom độc quyền được in trực tiếp lên sản phẩm sẽ giúp bạn hoàn toàn yên tâm sử dụng và bảo hành sản phẩm.","spec":null}',100,0);
insert into PRODUCT(BRANCH_ID, CATEGORY_ID, NAME, SUBTITLE, URL, PRICE, VIEW, SOLD, DETAIL, QUANTITY, VERSION) values(null,4,'Sạc Dự Phòng Microcom 8.000 mAh','','sac-du-phong-microcom-8000mah',249000,0,0,'{"images":{"selected":"default","items":{"default":"sac-du-phong-microcom-8000mah.png"}},"desc":"Pin Microcom với dung lượng 8000mAh đã được kiểm nghiệm và thực tế sử dụng hiệu suất của Pin đạt 90%, với tuổi thọ Pin sạc được hơn 500 lần. Mình đã kiểm chứng thực tế với iPhone 6 plus 2915mAh sạc được hơn 2 lần với Pin Microcom 8000mah, nhiều bạn thắc mắc với dòng ra 5v 2.1A liệu có ổn định lâu dài không? Bên trong Microcom 8000mah được điều khiển bằng IC điện tử chất lượng cao Hàn Quốc giúp kiểm soát được điện áp vào, điện áp ra. Tự động tắt khi sạc đầy pin cho thiết bị, tắt khi không sử dụng. Ngoài ra pin còn được trang bị hệ thông bảo vệ thông minh, chống nóng, chống cháy nổ và đi kèm công nghệ cells pin chất lượng cao từ LG, SamSung nên bạn hoàn toàn yên tâm về chất lượng. Hơn nữa nó còn tương thích với hầu hết các thiết bị điện tử của các hãng từ Smartphone, máy tính bảng, hay máy ảnh kỹ thuật số.v.v.. giúp bạn không phải mang theo loại pin nào khác.","spec":null}',100,0);
insert into PRODUCT(BRANCH_ID, CATEGORY_ID, NAME, SUBTITLE, URL, PRICE, VIEW, SOLD, DETAIL, QUANTITY, VERSION) values(null,4,'Sạc Dự Phòng Microcom 10.000 mAh','','sac-pdp-microcom-10000mah',299000,0,0,'{"images":{"selected":"default","items":{"default":"sac-pdp-microcom-10000mah.png"}},"desc":"Sạc Dự Phòng Microcom 10.000 mAh thiết kế sang trọng, hiện đại và tinh tế. Với màu sắc rất tinh tế và sang trọng cùng với kích thước vừa cầm tay sẽ giúp người dùng khá thoải mái khi cầm tay vừa sạc điện thoại vừa di chuyển. Với dung lượng 10.000 mAh, Pin sạc dự phòng có thể sạc đấy rất nhiều loại thiết bị như: điện thoại, máy nghe nhạc… Sản phẩm sử dụng lõi pin Samsung, LG khá bền và an toàn khi sử dụng, không làm giảm đi độ lưu giữ pin. Số lần sạc có thể lên đến hơn 500 lần mà không hề bị hao hụt dung lượng. Pin sạc dự phòng 10.000 mAh M10A còn được tích hợp thêm đèn pin tiện ích giúp bạn có thể sử dụng như chiếc đèn pin mini, chỉ cần vài thao tác cơ bản bạn sẽ dễ dàng bật/tắt đèn pin. Sản phẩm có 5 mức đèn báo hiệu dung lượng pin đang còn giúp bạn nhận biết khi nào cần sạc cho Pin sạc dự phòng","spec":null}',100,0);
insert into PRODUCT(BRANCH_ID, CATEGORY_ID, NAME, SUBTITLE, URL, PRICE, VIEW, SOLD, DETAIL, QUANTITY, VERSION) values(null,4,'Sạc Dự Phòng Arun 5600 mAh','','sac-du-phong-arun-5600-mah',149000,0,0,'{"images":{"selected":"default","items":{"default":"sac-du-phong-arun-5600-mah.png"}},"desc":"Sạc dự phòng arun 5600 mAh dung lượng chuẩn đạt từ 2800 – 3000 mAh. Pin sạc dự phòng ARUN 5600 mAh với thiết kế nhỏ gọn, có thể mang theo mọi lúc mọi nơi, cho phép cung cấp nguồn điện tức thời cho các thiết bị điện tử. Sản phẩm kèm cáp sạc, cho bạn sử dụng nhiều loại thiết bị khác nhau, từ iPod, iPhone, iPad, MP3… đến các loại điện thoại di động Nokia, Samsung, LG…Sản phẩm rất tiện lợi vì không có dây cáp dài vướng víu. Kết nối qua cổng USB tiện dụng. Sạc dự phòng Arun 5600mah được làm từ chất liệu nhựa ABS cao cấp, chịu được va đập.","spec":null}',100,0);
