insert into PRODUCT(BRANCH_ID, CATEGORY_ID, NAME, SUBTITLE, URL, PRICE, VIEW, SOLD, DETAIL, QUANTITY, VERSION) values(null,3,'Tai Nghe iPhone 7 Chính Hãng','','tai-nghe-iphone-7-zin-chinh-hang',550000,0,0,'{"images":{"selected":"default","items":{"default":"tai-nghe-iphone-7-zin-chinh-hang.png"}},"desc":"Khi ra mắt iPhone 7/7 Plus, Apple đã loại bỏ jack tai nghe 3.5mm và dùng chính cổng lightning để kết nối với tai nghe mang lại trải nghiệm nghe nhạc ấn tượng hơn. Tai nghe iphone 7 Apple EarPods Lightning sở hữu thiết kế tinh tế, hiện đại với kiểu dáng hợp thời trang và màu trắng thể hiện sự lịch lãm. Đầu tai nghe được làm nhỏ, áp sát và nằm gọn trong tai rất êm và thoải mái, không gây cảm giác mỏi, khó chịu khi đeo trong thời gian dài. Chiếc tai nghe do Apple sản xuất tương thích tốt với tất cả iPhone/iPad/iPod chạy hệ điều hành iOS 10 trở lên. Chỉ cần một chiếc Apple EarPods Lightning, người dùng có thể nghe nhạc thông qua rất nhiều thiết bị di động mang thương hiệu táo khuyết.","spec":null}',100,0);
insert into PRODUCT(BRANCH_ID, CATEGORY_ID, NAME, SUBTITLE, URL, PRICE, VIEW, SOLD, DETAIL, QUANTITY, VERSION) values(null,3,'Tai Nghe iPhone 5/6/6s Chính Hãng','','tai-nghe-iphone-566s-zin',299000,0,0,'{"images":{"selected":"default","items":{"default":"tai-nghe-iphone-566s-zin.png"}},"desc":"Tai nghe iPhone 5/6/6s là phụ kiện không thể thiếu với người dùng có sở thích nghe nhạc giải trí những khi rảnh rỗi. Tuy nhiên, để nâng cao trải nghiệm nghe nhạc, một trong những mẫu tai nghe nhạc phổ biến nhất hiện nay là Apple EarPods 3.5 mm, sản phẩm của Apple nhưng lại sở hữu giá bán rất phải chăng tại các hệ thống Di Động Thông Minh. Tai nghe Apple có cổng 3.5 mm Headphone tương thích nhiều thiết bị chứ không chỉ riêng sản phẩm apple. Hiện tại tai nghe được bán tại di động thông mình trên toàn quốc.","spec":null}',100,0);
insert into PRODUCT(BRANCH_ID, CATEGORY_ID, NAME, SUBTITLE, URL, PRICE, VIEW, SOLD, DETAIL, QUANTITY, VERSION) values(null,3,'Đầu Chuyển Tai Nghe Từ Cổng lightning Sang 3.5mm Chính Hãng','','dau-noi-tai-nghe-iphone-7',299000,0,0,'{"images":{"selected":"default","items":{"default":"dau-noi-tai-nghe-iphone-7.png"}},"desc":"Khi ra mắt iPhone 7/7 Plus, Apple đã loại bỏ jack cắm 3.5 mm và sử dụng tai nghe cắm trực tiếp vào cổng lightning, khiến người dùng gặp khó khăn trong việc kết nối điện thoại với những chiếc tai nghe cũ của họ. Vấn đề này sẽ được giải quyết hoàn toàn nếu như bạn mua một chiếc chuyển Apple Lightning sang 3.5 mm. Chiếc cáp này ra đời để phục vụ nhu cầu chuyển đổi khi sử dụng mọi thiết bị iOS của Apple, không phân biệt điện thoại, máy tính bảng hay máy nghe nhạc. Âm thanh được giữ hoàn toàn nguyên vẹn khi kết nối thông qua cáp chuyển, không hề có sự khác biệt với việc cắm trực tiếp vào cổng 3.5 mm. Tốc độ cáp chuyển Apple Lightning sang 3.5 mm Headphone rất ổn định","spec":null}',100,0);
insert into PRODUCT(BRANCH_ID, CATEGORY_ID, NAME, SUBTITLE, URL, PRICE, VIEW, SOLD, DETAIL, QUANTITY, VERSION) values(null,3,'Tai Nghe Bluetooth Xiaomi Sport','','tai-nghe-bluetooth-xiaomi-sport',699000,0,0,'{"images":{"selected":"default","items":{"default":"tai-nghe-bluetooth-xiaomi-sport.png"}},"desc":"Tai nghe Mi sport Bluetooth tai nghe thể thao, phong cách, chống nước hiệu quả. Tai nghe này có thiết kế hơi lớn so với các tai nghe Bluetooth khác với trọng lượng 17,8g. Nhưng chiếc Mi Sport này lại có dung lượng pin khá lớn 110mAh cho thời gian sử dụng. Mi Sport Bluetooth là tai nghe dành cho người tập thể dục. Vì vậy nó có thiết kế thêm 1 vòng đeo trên vành tai để tránh bị rơi khi di chuyển. Và với khả năng chống nước IPX4 nên nó có khả năng chống mồ hôi cũng như chống nước rất tốt. Mi Sports Bluetooth kết nối được với các smart phone thông qua Bluetooth 4.1, và đặc biệt nó có thể kết nối 2 thiết bị cùng 1 lúc.","spec":null}',100,0);
