insert into PRODUCT(BRANCH_ID, CATEGORY_ID, NAME, SUBTITLE, URL, PRICE, VIEW, SOLD, DETAIL, QUANTITY, VERSION) values(null,3,'Tai nghe Bluetooth Remax T9','','tai-nghe-bluetooth-remax-t9',280000,0,0,'{"images":{"selected":"default","items":{"default":"tai-nghe-bluetooth-remax-t9.png"}},"desc":"Thiết kế cao cấp và hiện đại, có chức năng stereo sàng lọc tiếng ồn, chống nhiễu tốt, có vòng đeo tay tiện lợi cho người sử dụng. Thời gian đàm thoại lên đến 6h, hỗ trợ nghe nhạc liên tục trong 5h. Có khả năng kết nối được cùng lúc 2 thiết bị. Là một chiếc tai nghe mới có thiết kế sang trọng và khá là bắt mắt, với phần vỏ nhựa thiết kế vảy cá khá độc đáo.","spec":null}',100,0);
