insert into PRODUCT(BRANCH_ID, CATEGORY_ID, NAME, SUBTITLE, URL, PRICE, VIEW, SOLD, DETAIL, QUANTITY, VERSION) values(null,3,'Tai nghe iPhone 6s zin chính hãng','','tai-nghe-iphone-6s-zin-chinh-hang',300000,0,0,'{"images":{"selected":"default","items":{"default":"tai-nghe-iphone-6s-zin-chinh-hang.png"}},"desc":"Tai nghe là phụ kiện không thể thiếu với những người dùng có sở thích nghe nhạc giải trí những khi rảnh rỗi. Tuy nhiên, để nâng cao trải nghiệm nghe nhạc, một trong những mẫu tai nghe nhạc phổ biến nhất hiện nay là Apple EarPods 3.5 mm, sản phẩm của “gã khổng lồ công nghệ” Apple nhưng lại sở hữu giá bán rất phải chăng. Tai nghe Apple EarPods 3.5 mm Headphone tương thích nhiều thiết bị chứ không chỉ riêng sản phẩm apple. Tai nghe của Apple được trang bị cụm phím điều chỉnh âm lượng đặt ở vị trí rất hợp lý, giúp người dùng dễ dàng kiểm soát việc bật/tắt nhạc. Tích hợp micro cho phép người dùng nhận cuộc gọi đến và đàm thoại ngay trên tai nghe mà không cần phải sử dụng điện thoại. Hiện tại tai nghe được bán tại di động thông mình trên toàn quốc, bảo hành 12 tháng 1 đổi 1.","spec":null}',100,0);
