insert into PRODUCT(BRANCH_ID, CATEGORY_ID, NAME, SUBTITLE, URL, PRICE, VIEW, SOLD, DETAIL, QUANTITY, VERSION) values(null,3,'Tai Nghe Xiaomi Lite 4.0','Chính Hãng','tai-nghe-xiaomi-lite-4-0',250000,0,0,'{"images":{"selected":"default","items":{"default":"tai-nghe-xiaomi-lite-4-0.png"}},"desc":"Là tai nghe nhét tai (Xiaomi In-Ear) ra mắt đầu năm 2016. Chất âm khỏe khoắn, mạnh mẽ. Driver Dynamic với công nghệ màng loa xếp kim loại composite. Hệ thống cân bằng âm thế hệ thứ 3, giảm sóc âm tốt và tự nhiên hơn. Thiết kế tối ưu cho việc cách âm và đeo một cách thoải mái nhất, vật liệu chống quá trình oxy hóa nhưng vẫn có màu sắc tinh khiết. Chất ấm được tối ưu hóa bởi màng loa kép, thiết kế sang chảnh, vỏ nhôm chống vân viền nhôm kim loại, Dây tròn chống rối. Tương thích với tất cả các điện thoại, máy tính bảng, máy nghe nhạc có jack 3.5mm","spec":null}',100,0);
insert into PRODUCT(BRANCH_ID, CATEGORY_ID, NAME, SUBTITLE, URL, PRICE, VIEW, SOLD, DETAIL, QUANTITY, VERSION) values(null,3,'Tai nghe Xiaomi Iron','','tai-nghe-xiaomi-iron',499000,0,0,'{"images":{"selected":"default","items":{"default":"tai-nghe-xiaomi-iron.png"}},"desc":"Xiaomi Mi Circle Iron có thiết kế hoàn toàn độc đáo sao với những dòng tai nghe tiền nhiệm nhưng vẫn giữ vẻ đẹp tinh tế đặc trưng Xiaomi. Phần thân housing được làm bằng kim loại sang trọng thay vì nhựa so với những thế hệ trước Piston 3.0 nhưng vẫn đảm bảo độ nhẹ khi mang trên tai. Chất lượng hoàn thiện được đánh giá ở mức tốt, trên tai nghe có lỗ thoát âm giúp tạo âm trường rộng, thoáng và không gian âm thanh. Cụm mic thoại, nút tăng giảm âm lượng và nút nhận cuộc gọi cũng được bọc bởi một vỏ kim loại chắc chắn. Mic thoại được trang bị khả năng chống ồn hiệu quả, hỗ trợ tùy chỉnh tăng giảm âm lượng tốt trên các thiết bị Android. Tai nghe Xiaomi Mi Circle Iron phát huy tốt sức mạnh của những nguồn phát như điện thoại, máy tính bảng, PC hay máy nghe nhạc chuyên dụng. Chất âm thiên hướng trầm ấn tượng phù hợp với các thể loại nhạc như dance, EDM, POP,..tái tạo âm thanh cực tốt. Âm bass mạnh mẽ, có tính lan tỏa nhưng không quá dập dồn.","spec":null}',100,0);
insert into PRODUCT(BRANCH_ID, CATEGORY_ID, NAME, SUBTITLE, URL, PRICE, VIEW, SOLD, DETAIL, QUANTITY, VERSION) values(null,3,'Tai nghe Samsung S8 Chính Hãng','','tai-nghe-samsung-s8-chinh-hang',249000,0,0,'{"images":{"selected":"default","items":{"default":"tai-nghe-samsung-s8-chinh-hang.png"}},"desc":"Tai nghe Samsung Galaxy S8 AKG là dòng phụ kiện kèm theo được đánh giá tốt. Điểm cộng của tai nghe là mang đến âm thanh chất lượng, có jack camứ 3.5mm tương thích nhiều thiết bị khác. Tai nghe Samsung Galaxy S8 AKG có thiết kế nhỏ gọn tinh tế, chất lượng âm thanh đáng khen trong tầm giá, có microphone, nút nhận cuộc gọi, tăng/giảm âm lượng, chuyển bài hát, Tai nghe in-ear có đệm tai mỏng và êm và là sản phẩm chính hãng của sam sung.","spec":null}',100,0);
insert into PRODUCT(BRANCH_ID, CATEGORY_ID, NAME, SUBTITLE, URL, PRICE, VIEW, SOLD, DETAIL, QUANTITY, VERSION) values(null,3,'Tai nghe Samsung S7 Chính Hãng','','tai-nghe-samsung-s7-chinh-hang',199000,0,0,'{"images":{"selected":"default","items":{"default":"tai-nghe-samsung-s7-chinh-hang.png"}},"desc":"Tai nghe là phụ kiện không thể thiếu đối với những người dùng có sở thích giải trí bằng âm nhạc. Nhưng để trải nghiệm nghe nhạc được trọn vẹn thì họ cần sở hữu sản phẩm đến từ các hãng tên tuổi. Tai nghe Samsung S7 là một lựa chọn hoàn hảo mà giá bán lại chỉ ở mức vừa phải. Tai nghe S7 có thiết kế in-ear với kiểu dáng trẻ trung, phần dây nối được làm dạng dẹt chống rối, ở giữa phần dây tai nghe Samsung Galaxy S7 là cụm phím điều khiển với 2 phím tăng giảm âm lượng và 1 phím nhận cuộc gọi. Tai nghe có tích hợp cả micro nên trong trường hợp đang nghe nhạc khi đi ngoài đường mà có cuộc gọi đến, bạn chỉ cần nhấn nút nhận cuộc gọi là có thể trả lời. Mọi thiết bị của Sony, HTC, LG hay Apple nếu hỗ trợ cổng tai nghe 3.5mm đều tương thích tốt với tai nghe Samsung Galaxy S7.","spec":null}',100,0);
insert into PRODUCT(BRANCH_ID, CATEGORY_ID, NAME, SUBTITLE, URL, PRICE, VIEW, SOLD, DETAIL, QUANTITY, VERSION) values(null,3,'Tai nghe Samsung Chính Hãng','','tai-nghe-samsung-chinh-hang',150000,0,0,'{"images":{"selected":"default","items":{"default":"tai-nghe-samsung-chinh-hang.png"}},"desc":"Tai Nghe Samsung Galaxy Chính Hãng dùng được cho tất cả các máy samsung: S5360,, s5830, s1, s2, s3, s4, s5, s6, note 1, note 2, note 3, note 4, i8150, i9250,… Ngoài ra tai nghe còn tương thích với hầu hết với điện thoại có cổng 3.5mm: HTC, iPhone, Máy tính bảng. Tai nghe có âm bass khá tốt, nghe hay và ấm. Thiết kế tai nghe dạng in-ear giúp các bạn tha hồ thưởng thức những bản nhạc hay với chất âm khá tốt. Tai nghe được thiết kế bo tròn ở đầu tai nghe và vành cao su được thiết kế nhỏ hơn và chắc chắn hơn. Với tai nghe các bạn sẽ được tận hưởng những bản nhạc hay với âm thanh vòm 5.1.","spec":null}',100,0);
insert into PRODUCT(BRANCH_ID, CATEGORY_ID, NAME, SUBTITLE, URL, PRICE, VIEW, SOLD, DETAIL, QUANTITY, VERSION) values(null,3,'Tai nghe HTC Chính Hãng','','tai-nghe-htc-chinh-hang',150000,0,0,'{"images":{"selected":"default","items":{"default":"tai-nghe-htc-chinh-hang.png"}},"desc":"Tai nghe chính hãng HTC chất lượng âm thanh tuyệt vời đặt biệt là ở âm bass cực mạnh, treble êm, bật max volume không hề có hiện tượng bị rè. Kiểu dáng in-ear nhét tai nhỏ gọn, dây dẹt có độ bền cao không lo bị đứt, hoặc dây tai nghe bị rối, sử dụng jack cắm 3.5mm phù hợp với hầu hết các loại smartphone, tablet, MP3, MP4, laptop… Hơn nữa, với chiều dài cáp 1.2m thuận tiện để bạn kết nối với các thiết bị ở bất cứ đâu. Bộ đệm tai mềm, vừa khít với tai nghe tạo cảm giác thoải mái và cách âm tốt. Tương thích với mọi điện thoại, máy tính bảng, máy nghe nhạc…","spec":null}',100,0);
